# CCSDS Library Exceptions


class CCSDSException(Exception):
    def __init__(self, message):
        self.message = message

# Problems working with the CMD Buffer


class CmdBufferException(CCSDSException):
    def __init__(self, message):
        super().__init__(message)

# Config value not found


class InvalidFrameData(CCSDSException):
    def __init__(self, message):
        super().__init__(message)

# Config value not found


class InvalidTransferFrameHeaderVal(CCSDSException):
    def __init__(self, message):
        super().__init__(message)

# Problems with the block size


class InvalidBlockSize(CCSDSException):
    def __init__(self, message):
        super().__init__(message)
